import React from 'react';
import '../../node_modules/bootstrap/dist/css/bootstrap.css'
import './style.css'
import { timesSeries } from 'async';

class ProductPage extends React.Component {
  
    render() { 
        return ( 
            <div className="item">
            <div className="image">
            <img src={this.props.productUrl}/>

            </div>
            <div className="midle aligned content">
             <div className='header'>
             <a>
                 
                 
                 </a>
                 {this.props.votes}
             
             </div>
            <div className="description">
                <a href={this.props.url} >{this.props.title}</a>
                <p>{this.props.description}</p>
            </div>
            <div className="extra">
                <span>Submitted by:</span>
                <img className="voterimage" src={this.props.submitterUrl}></img>

            </div>
            <div className="vote">
                <button class="btn btn-primary" onClick={this.props.onUpVote} >Up vote </button> &nbsp;
                <button class="btn btn-primary" onClick={this.props.onDownVote}>Down vote</button>
            </div>
            </div>
            </div>
            
         );
    }
}
 
export default ProductPage;
