import React from 'react';
import ProductPage from './ProductPage';
import axios  from 'axios';

class ProductList extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            users: []
        }
    }
    getRemoteData(){
        axios.get('http://localhost:3000/productsList')
        .then((received)=>{
            this.setState({users: received.data})
            console.log(this.state.users);
            
        })
    }
    
    componentWillMount(){
        this.getRemoteData()
    }
    upVoteClicked(){
        console.log("upvote Clicked");
        
    }
    downVoteClicked(){
        console.log("down Clicked");
        
    }

    renderProductData(){
        
        return this.state.users.map((seed) => {
            return (  
               
                <ProductPage 
                id={seed.id}
                title={seed.title}
                description={seed.description}
                upvote={seed.upvote}
                downvote={seed.downvote}
                submitterUrl={seed.submitterUrl}
                productUrl={seed.productUrl}
                onUpVote={this.upVoteClicked}
                onDownVote={this.downVoteClicked}
                ></ProductPage>)
                
        });
    }
    render() { 
        return (  
            <div className="ui unstackable items">
                {this.renderProductData()}
            </div>
        );
    }
}
 
export default ProductList;
